cpp_code:
int majority_branch(int x, int y, int z) {
    int r = 0;
    int m1 = (x & y) != 0 ? 1 : 0;
    int m2 = (y & z) != 0 ? 1 : 0;
    int m3 = (x & z) != 0 ? 1 : 0;
    int m = m1 + m2 + m3;
    if (m & 1) {
        r = (x & y) | (y & z) | (x & z);
    } else {
        r = (x & y) | (y & z) | (x & z);
    }
    return r;
}

